mapper.update:

UPDATE mymember 
			 SET  mem_name = #{memName}
			    , mem_tel  = #{memTel}
			    , mem_addr = #{memAddr}
			    , atch_file_id = #{atchFileId}
			WHERE mem_id   = #{memId}

mapper.select:

select count(*) as cnt
from mymember
where mem_id =#{memId}

select * from mymember
where 1=1

	 and mem_id = #{memId}


	 and mem_name = #{memName}


	 and mem_tel = #{memTel}


	 and mem_addr like '%' ||  #{memAddr}  || '%'
